import React, { useState } from 'react';

function Bill(props) {
	const [edit, setEdit] = useState(false);
	// const date = props.due_date

	function getDay(date) {
		const d = new Date(date);
		const day = d.getDate();
		const month = (d.getMonth() + 1)

	  return month + '/' + day;
	}

	const formatedDay = getDay(props.due_date)

	const goToWebPage = () => {
		window.open(
              props.bill_website, "_blank");
	}

	const handelEdit = () => {
		setEdit(props.bill_id)
	}

	return (
			<div>
				<p className="fl w-25 pa1 tc ml1">{props.bill_name}</p> 
				<p className="fl w-25 pa1 tc">{props.amount}</p> 
				<p className="fl w-25 pa1 tc">{formatedDay}</p>
				<button className="fl pa1" onClick={goToWebPage}> Pay </button>
				<button className="fl pa1" onClick={handelEdit}>Edit</button>
			</div>
		);

}

export default Bill;
