import React, { useState } from 'react';

function Bill(props) {
	const { bill_name, bill_website, due_date, host} = props;
	const [edit, setEdit] = useState(false);
	const [amount, setAmount] = useState(props.amount)
	const [website, setWebsite] = useState(bill_website)
	const [name, setName] = useState(bill_name)
	// const date = props.due_date

	function getDay(date) {
		const d = new Date(date);
		const day = d.getDate();
		const month = (d.getMonth() + 1)

	  return month + '/' + day;
	}

	const formatedDay = getDay(due_date)

	const goToWebPage = () => {
		window.open(
              bill_website, "_blank");
	}

	const handelEdit = () => {
		setEdit(!edit)
	}

	const onNameChange = (event) => {
		setName(event.target.value)
	}

	const onAmountChange = (event) => {
		setAmount(event.target.value)
	}

	const onWebsiteChange = (event) => {
		setWebsite(event.target.value)
	}

	const onUpdateBill = () => {
		console.log(JSON.stringify({
			"bill_name": name, 
			"amount": amount, 
			"bill_website": website
		})
		)
		handelEdit()
	}

	return (
		<div>
		{ !edit
			? <div>
					<p className="fl w-25 pa1 tc ml1">{name}</p> 
					<p className="fl w-25 pa1 tc">{amount}</p> 
					<p className="fl w-25 pa1 tc">{formatedDay}</p>
					<br></br>
					<p className="fl w-25 pa1 tc">{website}</p>
					<button className="fl pa1" onClick={goToWebPage}> Pay </button>
					<button className="fl pa1" onClick={handelEdit}>Edit</button>
				</div>
			:
				<div>
					<input 
					className= 'fl w-25 pa1 tc ml1' 
					type='text' 
					id='name'
					name='name'
					value={bill_name}
					onChange={onAmountChange}
					/>
					<input 
					className= 'fl w-25 pa1 tc ml1' 
					type='double' 
					id='amount'
					name='amount'
					value={amount}
					onChange={onAmountChange}
					/>
					<input 
					className= 'fl w-25 pa1 tc ml1' 
					type='text' 
					id='website'
					name='website'
					value={website}
					onChange={onWebsiteChange}
					/>
					<button onClick={onUpdateBill}>Update </button>
				</div>
		}
		</div>

		);

}

export default Bill;
