import React, { useState } from 'react';
import { DropdownButton, Dropdown } from 'react-bootstrap/'

const InputTransaction = () => {
	const [date, setDate] = useState('')
	const [vendor, setVendor] = useState('')
	const [amount, setAmount] =	useState('')
	const [status, setStatus] = useState('')
	const [category, setCategory] = useState('')

	const onDateChange = (event) => {
		setDate(event.target.value)
	}

	const onVendorChange = (event) => {
		setVendor(event.target.value)
	}

	const onAmountChange = (event) => {
		setAmount(event.target.value)
	}

	const onStatusChange = (event) => {
		setStatus('Cleared')
		console.log(status)
	}

	const onCategoryChange = (event) => {
		setCategory(event.target.value)
	}

	const onCommitTransaction = () => {
		fetch('http://localhost:3000/insert', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "date": date,
			  "vendor": vendor,
			  "category": category,
			  "status": status,
			  "amount": amount
			})
		})
			.then(response => response.json())
	}

	function myFunction() {
	  document.getElementById("myDropdown").classList.toggle("show");
	}

	// Close the dropdown menu if the user clicks outside of it
	window.onclick = function(event) {
	  if (!event.target.matches('.dropbtn')) {
	    var dropdowns = document.getElementsByClassName("dropdown-content");
	    var i;
	    for (i = 0; i < dropdowns.length; i++) {
	      var openDropdown = dropdowns[i];
	      if (openDropdown.classList.contains('show')) {
	        openDropdown.classList.remove('show');
	      }
	    }
	  }
	}

	return (

			<div className='center'>
				<div className='form center pa4 br3 shadow-5'>
				<input 
					className= 'f4 pa2 w-70 center' 
					type='date' 
					id='date'
					name='date'
					placeholder="Date dd/mm/yyyy" 
					onChange={onDateChange}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder="Vendor"
					id='Vendor'
					name='Vendor'
					onChange={onVendorChange}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'double' 
					placeholder="Amount"
					id='Amount'
					name='Amount'
					onChange={onAmountChange}
				/>
				<DropdownButton 
					id="dropdown-basic-button" 
					title="Dropdown button"
					className= 'f4 w-70 center'
				>
				  <Dropdown.Item href="#/action-1">Cleared</Dropdown.Item>
				  <Dropdown.Item href="#/action-2">Pending</Dropdown.Item>
				</DropdownButton>

				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'tex' 
					placeholder="Category"
					id='Category'
					name='Category'
					onChange={onCategoryChange}
				/>
				<button 
					className= 'w-30 grow f4 link ph3 pv2 dib white bg-light-blue' 
					id='CommitTransactionBTN'
					onClick={onCommitTransaction}
				>
				Commit
				</button>
				</div>
			</div>
	);
}

export default InputTransaction;
