		console.log(props.setCount)
	}

	const formatedDate = getFormattedDate(date)

	return (
		<div className="flex justify-center">
			<p className="fl w-10 tc">{formatedDate}</p> 
			<p className="fl w-20 tc">{vendor}</p> 
			<p className="fl w-10 tc">{`$ ${amount}`}</p> 
			<button 
				className="fl w-10 tc bg-white "
				onClick={() => handelStatus(id)}
			>{status}
