import React, { useState, useEffect } from 'react';
import './App.css';
import CardList from './components/Transactions/CardList.js';
//import SignIn from './components/SignIn/SignIn.js';
import Navigation from './components/Navigation/Navigation.js';
import Greeting from './components/Greeting/Greeting.js';
import UpcomingBills from './components/UpcomingBills/UpcomingBills.js';
import InputTransaction from './components/InputTransaction/InputTransaction.js';

function App() {
	const host = 'http://localhost:3000';
	const name = 'Adam';
	const [transactions, setTransactions] = useState([]);
	const [count, setCount] = useState(0)

	useEffect(() => {
		fetch('http://localhost:3000/transactions')
			.then(response => response.json())
	      	.then(data => {setTransactions(data)});
	},[])

  return (
  	<div className="App">
  		<h1>count</h1>
    	<div className="pa4">
	      <Navigation/>
	      <Greeting name={name} />
	      <InputTransaction host={host} />
	    </div>
      <div className="flex flex-wrap no-wrap">
      	<div className="outline w-25 pa3 mr2 ml2">
	      	<UpcomingBills/>
	      </div>
	      <div className="outline w-70 pa3 mr2">
		      <CardList transactions = {transactions}
		      />
		    </div>
      </div>
    </div>
  );
}

export default App;
