import React from 'react';
import './Greeting.css'

const Greeting = (props) => {

	let pendingSum = 0;
	let sum = 0;
	let color = 'red';
	// const begingBalance = 7429.10;

	if (props.pendingSum === null) {
		pendingSum = 0 
	}else {
		pendingSum = props.pendingSum
	}

	if (props.sum === null) {
		sum = 0 
	}else {
		sum = props.sum
	}

	if (sum < 0) {
		color = 'red' 
	}else {
		color = 'red'
	}

	return (
		<div>
		  <h1 className='f3'>
		    {`${props.name}, your account ballance is ...`}
		  </h1>
		  	<p className={color}>
		  		{`Pending ${pendingSum} Actual ${sum}`}
		  	</p>
		</div>
	);
}

export default Greeting;
