import React, { useState, useEffect } from 'react';
import './App.css';
import CardList from './components/Transactions/CardList.js';
//import SignIn from './components/SignIn/SignIn.js';
import Navigation from './components/Navigation/Navigation.js';
import Greeting from './components/Greeting/Greeting.js';
import UpcomingBills from './components/UpcomingBills/UpcomingBills.js';
import InputTransaction from './components/InputTransaction/InputTransaction.js';
import Scroll from './components/Scroll/Scroll.js';
import InsertBill from './components/UpcomingBills/InsertBill.js'

function App() {
	// const host = 'https://nameless-refuge-41424.herokuapp.com';
	const host = 'http://localhost:3000'
	const [name, setName] = useState('Adam');
	const [pendingSum, setPendingSum] = useState('Loading')
	const [sum, setSum] = useState('Loading')
	const [transactions, setTransactions] = useState([]);
	const [submit, setSubmit] = useState(false);
	const [insert, setInsert] = useState(false);
	const [insertNewBill, setinsertNewBill] = useState(false);
	const [userid, setUserId] = useState(1);
	const [accountid, setAccountid] = useState(1);

	const handleInputChange = () => {
		// console.log(`handle Input ${submit}`)
		setSubmit(!submit)
	}

	const handleInsertChange = () => {
		// console.log(insert);
		setInsert(!insert);

	}

	const switchUser = () => {
		setUserId(2);
		setName('Test');
		setSubmit(true)
	}

	useEffect(() => {
		try {
			fetch(`${host}/sumCleared`)
				.then(response => response.json())
		      	.then(data => setSum(data[0].sum));

		    fetch(`${host}/sumPending`)
				.then(response => response.json())
		      	.then(data => {setPendingSum(data[0].sum)});

		    fetch(`${host}/transactions`, {
				method: 'put',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
				  "accountid": accountid
				})
			})
				.then(response => response.json())
		      	.then(data => {setTransactions(data)});

		} catch (error) {
			console.error(error)
		} finally {
		    // console.log(`effect ${submit}`)
	    	setSubmit(false)
		}
	},[submit, userid])	

  return (
  	<div className="App">
    	<div className="pa4">
	      <Navigation/>
	      <Greeting name={name} sum={sum} pendingSum={pendingSum} />
	      { insertNewBill === false
	      	? <InsertBill host={host} />
	      	: <button >New Bill </button>
	      } 

	    </div>
      <div className="flex flex-row no-wrap">
      	<div className="outline w-25 pa2 mr2 ml2">
	      	<UpcomingBills host={host}/>
	    </div>
	      <div className="outline w-70 pa3 mr2">
		      <div>
				{ insert === true 
	      		?<div>
			      	<InputTransaction 
			      		userid={userid}
				      	host={host} 
				      	submit={submit} 
				      	onChange={handleInputChange} 
				    />
						<button onClick={handleInsertChange}> Exit </button>
					    </div>
					    : 
					    <button onClick={handleInsertChange}> Insert </button>
					  }
					</div>
	      	<Scroll>
			    <CardList 
			      	transactions={transactions} 
			      	userid={userid}
			      	host={host} 
			      	submit={submit} 
		      		onChange={handleInputChange} 
			    />
			</Scroll>
		  </div>
      </div>
    </div>
  );
}

export default App;
