import React, { useState, useEffect } from 'react';
import Select from 'react-select';

function Search(props) {
	const [categories, setCategories] = useState([])
	const categoryList = [];
	const [catLabel, setCatLabel] = useState(props.category)

	const handleChange = (data) => {
		props.onChange(data.value, data.label)
		setCatLabel(data.label)

	}

	useEffect(() => {
		try {
		fetch(props.host+'/category')
			.then(response => response.json())
	      	.then(data => {
	      		data.map(i => {
	      			categoryList.push({label: i.category, value: i.catid})
	      		})
	      	})
	      	.then(setCategories(categoryList));
	    } catch (error) {
			console.error(error)
		}
	},[])

	return (
		<div className='w-30'>
		  <Select
		  	// value={catLabel}
		  	onChange={handleChange}
		    options={categories}
		  />
		</div>
	);
}

export default Search;
