import { useState, useEffect } from 'react'

export default function useBalance(account_id, host, submit) {
	const [balance, setBalance] = useState();

	useEffect(() => {
		if (account_id) {
			try {
				fetch(`${host}sumCleared`, {
					method: 'post',
					headers: {'Content-Type': 'application/json'},
					body: JSON.stringify({
					  "account_id": account_id
					})
				})
					.then(response => response.json())
			      	.then(data => setBalance(data[0].sum));

			} catch (error) {
				console.error(error)
			} 		
			if (balance < 0) {
				setBalanceColor('red')
			}else {setBalanceColor('black')}
		}

	},[account_id, balance, host, submit])	

	return [balance, balanceColor];
}
