import React, { useState } from 'react';

const SignIn = (props) => {
	const [signInEmail, setSignInEmail] = useState('');
	const [signInPassword, setSignInPassword] = useState('')
	const [loading, setLoading] = useState(false);
	const [missingPassword, setMissingPassword] = useState(false);

	const onEmailChange = (event) => {
		setSignInEmail(event.target.value);
	}

	const onPasswordChange = (event) => {
		setSignInPassword(event.target.value);
	}

	const onSubmitSignIn = () => {
		if (signInEmail && signInPassword) {
			setLoading(true);
			fetch(props.host + 'signin', {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					email: signInEmail,
					password: signInPassword
				})
			})
				.then(response => response.json())
				.then(user => {
					if (user !== 'wrong credentials') {
						props.onChange(user[0].user_id, user[0].firstname);
					} else {
						props.onChange(-1, -1)
					}
				})
		}
		else if (!signInEmail) {
			console.log("missing email!!");
		}
		else if (!signInPassword) {
			console.log("missing password!!");
			setMissingPassword(true);
		}
	}
	return (
		<article className="br3 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
			<main className="pa4 black-80">
			  <div className="measure">
			    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
			      <legend className="f1 fw6 ph0 mh0">Sign In</legend>
			      <div className="mt3">
			        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
			        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address" id="email-address" onChange={onEmailChange} required/>
			      </div>
			      <div className="mv3">
			        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
			        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password" id="password" onChange={onPasswordChange} required/>
			        { missingPassword ? <p>Missing Password</p> : <p></p>}

			      </div>
			      <label className="pa0 ma0 lh-copy f6 pointer"></label>
			    </fieldset>
			    <div className="">
			      <input onClick={onSubmitSignIn} className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value={!loading ? "Sign in" : "Loading"} />
			    </div>
			    <div className='lh-copy mt3'>
			      <p onClick={() => props.onRegisterChange('register')} className='f6 link dim black db pointer'>Register</p>
			    </div>
			  </div>
			</main>
		</article>
	);
}

export default SignIn;
