import React, { useState, useEffect } from 'react';
import useBalance from '../../Hooks/useBalance.js';
import './AccountBalance.css';

const AccountBalance = (props) => {
	const [balance, setBalance] = useBalance(props.account_id, props.host);
	const [balanceColor, setBalanceColor] = useState('black')

	useEffect(() => {
		if (props.account_id) {
			try {

				fetch(`${props.host}sumCleared`, {
					method: 'post',
					headers: {'Content-Type': 'application/json'},
					body: JSON.stringify({
					  "account_id": props.account_id
					})
				})
					.then(response => response.json())
			      	.then(data => setBalance(data[0].sum));

			} catch (error) {
				console.error(error)
			} 		
			if (balance < 0) {
				setBalanceColor('red')
			}else {setBalanceColor('black')}
		}

	},[props.account_id, balance, props.host, props.submit])	

	return (
	  	<p className={balanceColor}>{`$ ${((Math.round(balance) * 100) / 100).toFixed(2)}`} </p>	
	);
}

export default AccountBalance;
