import React, { Component } from 'react';

class InputTransaction extends Component {
	constructor(props) {
		super(props);
		this.state = {
			date: '',
			vendor: '',
			amount: ''
		}
	}

	onDateChange = (event) => {
		console.log(event.target.value)
	}

	onVendorChange = (event) => {
		console.log(event.target.value)
	}

	onAmountChange = (event) => {
		console.log(event.target.value)
	}

	render() {
		return (
			<div>
				<div className='center'>
					<div className='form center pa4 br3 shadow-5'>
					<input 
						className= 'f4 pa2 w-70 center' 
						type='date' 
						id='date'
						name='date'
						placeholder="Date dd/mm/yyyy" 
						onChange={this.onDateChange}
					/>
					<input 
						className= 'f4 pa2 w-70 center' 
						type= 'tex' 
						placeholder="Vendor"
						id='Vendor'
						name='Vendor'
						onChange={this.onVendorChange}
					/>
					<input 
						className= 'f4 pa2 w-70 center' 
						type= 'tex' 
						placeholder="Amount"
						id='Amount'
						name='Amount'
						onChange={this.onAmountChange}
					/>
					<input className= 'f4 pa2 w-70 center' type= 'tex' placeholder="Status"/>
					<input className= 'f4 pa2 w-70 center' type= 'tex' placeholder="Category"/>
					<button 
					className= 'w-30 grow f4 link ph3 pv2 dib white bg-light-blue' 
					>Detect</button>
					</div>
				</div>
			</div>
		);
	}
}

export default InputTransaction;
