import React, { useState, useEffect } from 'react';
import './Greeting.css'

const Greeting = (props) => {
	const [pendingSum, setPendingSum] = useState('Loading')
	const [sum, setSum] = useState('Loading')
	const account_id = "daee290c-f60b-44c8-a3bb-4005e7854b98"

	let sumcolor = 'black';
	let pendingcolor = 'black';
	// const begingBalance = 7429.10;

	useEffect(() => {
		try {
			fetch(`${props.host}/sumCleared`, {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
				  "account_id": account_id
				})
			})
				.then(response => response.json())
		      	.then(data => setSum(data[0].sum));

		    fetch(`${props.host}/sumPending`, {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
				  "account_id": account_id
				})
			})
				.then(response => response.json())
		      	.then(data => {setPendingSum(data[0].sum)});

		} catch (error) {
			console.error(error)
		} 
		// finally {
	 //    	setSubmit(false)
		// }
	},[props.userid])	


	if (pendingSum === null) {
		setPendingSum("Loading") 
	}

	if (sum === null) {
		sum = 0 
	}

	if (sum < 0) {
		sumcolor = 'red' 
	}else {
		sumcolor = 'black'
	}

	if (pendingSum < 0) {
		pendingcolor = 'red' 
	}else {
		pendingcolor = 'black'
	}

	return (
		<div>
		  <h1 className='f3'>
		    {`${props.name}, your account ballance is ...`}
		  </h1>
		  	<p className={pendingcolor}>
		  		{`Pending $ ${pendingSum}`}
		  	</p>
		  	<p className={sumcolor}>{`Actual $ ${sum}`}
		  	</p>
		</div>
	);
}

export default Greeting;
