import React from 'react';

function Bill(props) {
	const date = props.due_date

	function getDay(date) {
		const d = new Date(date);
		const day = d.getDate();
		const month = (d.getMonth() + 1)

	  return month + '/' + day;
	}

	const formatedDay = getDay(date)

	const goToWebPage = () => {
		window.open(
              "https://www.geeksforgeeks.org", "_blank");
	}

	return (
			<div>
				<p className="fl w-25 pa1 tc">{props.bill_name}</p> 
				<p className="fl w-25 pa1 tc">{props.amount}</p> 
				<p className="fl w-25 pa1 tc">{formatedDay}</p>
				<button className="fl " onClick={goToWebPage}> Pay </button>
			</div>
		);

}

export default Bill;
