import React from 'react';

function Bill(props) {
	const date = props.duedate

	function getDay(date) {
		const d = new Date(date);
		const day = d.getDate();

	  return day;
	}

	const formatedDay = getDay(date)

	return (
			<div>
				<p className="fl w-25 pa1 tc ml2">{props.billname}</p> 
				<p className="fl w-30 pa1 tc">{props.amount}</p> 
				<p className="fl w-25 pa1 tc">{formatedDay}</p>
			</div>
		);

}

export default Bill;
