import React from 'react';
import './Card.css';

const Card = ({ id, date, vendor, amount, status, category, host }) => {

	function getFormattedDate(date) {
		const d = new Date(date);

	  const year = d.getFullYear();
	  const month = (d.getMonth() + 1)
	  const day = d.getDate();

	  return month + '/' + day + '/' + year;
	}

	const handelStatus = (id) => {
		console.log(id+' clicked')
	}

	const handelRemove = (id) => {
		console.log(id+" remove");
		fetch(host+'/remove', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "date": date,
			  "vendor": vendor,
			  "category": category,
			  "status": status,
			  "amount": amount
			})
		})
			.then(response => response.json())
			.catch(console.log)
		// props.setCount(!props.count)
	}

	date = getFormattedDate(date)

	return (
		<div className="flex justify-center">
			<p className="fl w-10 tc">{date}</p> 
			<p className="fl w-20 tc">{vendor}</p> 
			<p className="fl w-10 tc">{`$ ${amount}`}</p> 
			<button 
				className="fl w-10 tc bg-white "
				onClick={() => handelStatus(id)}
			>{status}
			</button> 
			<p className="fl w-10 tc">{category}</p>
			<button 
				className="fl w-10 tc bg-white" 
				onClick={() => handelRemove(id)}>remove
			</button>
		</div>
		);

}

export default Card;
