import React, { useState } from 'react';
// import { useForm } from "react-hook-form";

const InputTransaction = (props) => {
	const [date, setDate] = useState('')
	const [vendor, setVendor] = useState('')
	const [amount, setAmount] =	useState('')
	const [status, setStatus] = useState('Pending')
	const [category, setCategory] = useState('')

	const onDateChange = (event) => {
		setDate(event.target.value)
	}

	const onVendorChange = (event) => {
		setVendor(event.target.value)
	}

	const onAmountChange = (event) => {
		setAmount(event.target.value)
	}

	const onStatusChange = (event) => {
		status === 'Pending' ? setStatus('Cleared') : setStatus('Pending')
	}

	const onCategoryChange = (event) => {
		setCategory(event.target.value)
	}

	const onCommitIncome = () => {
		onCommitTransaction(amount)
	}

	const onCommitExpense = () => {
		onCommitTransaction(setAmount = amount * -1)
		//onCommitTransaction(amount)
	}

	const onCommitTransaction = (amount) => {
		fetch(props.host+'/insert', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "date": date,
			  "vendor": vendor,
			  "category": category,
			  "status": status,
			  "amount": amount
			})
		})
			.then(response => response.json())
			.catch(console.log)
		setVendor('')
		setDate('')
		setAmount('')
		setCategory('')
		// console.log(`insert ${props.submit}`)
		props.onChange()

	}

	return (
		<div className='center'>
			<div className='form center pa4 br3 shadow-5'>
				<input 
					className= 'f4 pa2 w-70 center' 
					type='date' 
					id='date'
					name='date'
					value={date}
					onChange={onDateChange}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder="Vendor"
					id='Vendor'
					name='Vendor'
					value={vendor}
					onChange={onVendorChange}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'double' 
					placeholder="Amount"
					id='Amount'
					name='Amount'
					value={amount}
					onChange={onAmountChange}
				/>
				<button 
					className= 'w-30 link ph3 pv2 dib bg-white' 
					id='CommitTransactionBTN'
					onClick={onStatusChange}
				>
				{status}
				</button>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'tex' 
					placeholder="Category"
					id='Category'
					name='Category'
					value={category}
					onChange={onCategoryChange}
				/>
				<button 
					className= 'w-30 grow f4 link ph3 pv2 dib white bg-light-blue' 
					id='CommitTransactionBTN'
					onClick={onCommitExpense}
				>
				Expense
				</button>
				<button 
					className= 'w-30 grow f4 link ph3 pv2 dib white bg-light-blue' 
					id='CommitTransactionBTN'
					onClick={onCommitIncome}
				>
				Income
				</button>
			</div>
		</div>
	);
}

export default InputTransaction;
