import React from 'react';

function Bill(props) {
	const date = props.duedate

	function getDay(date) {
		const d = new Date(date);
		const day = d.getDate();

	  return day;
	}

	const formatedDay = getDay(date)

	return (
		<div className="flex justify-center">
			<div>
				<p className="fl w-50 tc">{props.billname}</p> 
				<p className="fl w-50 tc">{props.amount}</p> 
				<p className="fl w-50 tc">{formatedDay}</p>
			</div>
		</div>
		);

}

export default Bill;
