import React, { useState, useEffect } from 'react';
import Card from './Card.js'

const CardList = (props) => {
	//let count = 0;
	const [accountid, setAccountid] = useState(1);
	const { transactions } = props
	const updateTransactions = (value) => {
		props.onChange()
		//console.log(`CardList ${value}`)
	}

	useEffect(() => {
		fetch(`${host}/transactions`, {
			method: 'put',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "accountid": accountid
			})
		})
		.then(response => response.json())
      	.then(data => {setTransactions(data)});
	},[])

	return (
		<div>
			<div className="flex justify-center">
				<h3 className="fl w-10 pa1 tc">Date</h3> 
				<h3 className="fl w-20 pa1 tc">Vender</h3> 
				<h3 className="fl w-10 pa1 tc">Amount</h3> 
				<h3 className="fl w-10 pa1 tc">Status</h3> 
				<h3 className="fl w-10 pa1 tc">Category</h3>
				<h3 className="fl w-10 pa1 tc">remove</h3>
			</div>
			{
			transactions.map((data, i) => {
				return (
					<Card 
					key={i}
					transaction_id={transactions[i].transaction_id} 
					accountid={transactions[i].accountid}
					date = {transactions[i].date}
					vendor={transactions[i].vendor}
					amount={transactions[i].amount}
					status={transactions[i].status}
					category={transactions[i].category}
					host={props.host}
					onChange={updateTransactions}
					submit={props.submit}
					/>
				);
			})
			}
		</div>
		);
}

export default CardList;
