import React, { useState, useEffect } from 'react';
import './Greeting.css'

const Greeting = (props) => {
	const [pendingSum, setPendingSum] = useState('Loading')
	const [sum, setSum] = useState('Loading')
	const [sumColor, setSumColor] = useState('black')
	const [pendingColor, setPendingColor] = useState('black')
	// let sumcolor = 'black';
	// let pendingcolor = 'black';
	// const begingBalance = 7429.10;

	const account_id = "daee290c-f60b-44c8-a3bb-4005e7854b98"

	useEffect(() => {
		try {
			fetch(`${props.host}/sumCleared`, {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
				  "account_id": account_id
				})
			})
				.then(response => response.json())
		      	.then(data => setSum(data[0].sum));

		    fetch(`${props.host}/sumPending`, {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
				  "account_id": account_id
				})
			})
				.then(response => response.json())
		      	.then(data => {setPendingSum(data[0].sum)});

		} catch (error) {
			console.error(error)
		} 

		if (pendingSum === null) {
			setPendingSum("Loading") 
		}

		if (sum === null) {
			setSum("Loading") 
		}

		// if (sum < 0) {
		// 	setSumColor('red')
		// }

		// if (pendingSum < 0) {
		// 	setPendingColor('red') 
		// }
	},[props.userid, props.submit])	




	return (
		<div>
		  <h1 className='f3'>
		    {`${props.name}, your account ballance is ...`}
		  </h1>
		  	<p className={pendingColor}>
		  		{`Pending $ ${pendingSum}`}
		  	</p>
		  	<p className={sumColor}>{`Actual $ ${sum}`}
		  	</p>
		</div>
	);
}

export default Greeting;
