						className= 'f4 pa2 w-70 center' 
						type= 'text' 
						placeholder="Amount"
						id='Amount'
						name='Amount'
						onChange={this.onAmountChange}
					/>
					<input 	
						className= 'f4 pa2 w-70 center' 
						type= 'text' 
						placeholder="Status"
						id='Status'
						name='Status'
						onChange={this.onStatusChange}						
					/>
					<input 
						className= 'f4 pa2 w-70 center' 
						type= 'tex' 
						placeholder="Category"
						id='Category'
						name='Category'
						onChange={this.onCategoryChange}
