import React, { useState } from 'react';

const InputTransaction = () => {
	const [date, setDate] = useState(Date.now())
	const [vendor, setVendor] = useState('')
	const [amount, setAmount] =	useState('')
	const [status, setStatus] = useState('')
	const [category, setCategory] = useState('')

	const onDateChange = (event) => {
		setDate(event.target.value)
	}

	const onVendorChange = (event) => {
		setVendor(event.target.value)
	}

	const onAmountChange = (event) => {
		setAmount(event.target.value)
	}

	const onStatusChange = (event) => {
		setStatus(event.target.value)
	}

	const onCategoryChange = (event) => {
		setCategory(event.target.value)
	}

	const onCommitTransaction = () => {
		console.log(date, vendor, amount, status, category)
	}
	return (

			<div className='center'>
				<div className='form center pa4 br3 shadow-5'>
				<input 
					className= 'f4 pa2 w-70 center' 
					type='date' 
					id='date'
					name='date'
					placeholder="Date dd/mm/yyyy" 
					onChange={onDateChange}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder="Vendor"
					id='Vendor'
					name='Vendor'
					onChange={onVendorChange}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'double' 
					placeholder="Amount"
					id='Amount'
					name='Amount'
					onChange={onAmountChange}
				/>
				<input 	
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder="Status"
					id='Status'
					name='Status'
					onChange={onStatusChange}						
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'tex' 
					placeholder="Category"
					id='Category'
					name='Category'
					onChange={onCategoryChange}
				/>
				<button 
					className= 'w-30 grow f4 link ph3 pv2 dib white bg-light-blue' 
					id='CommitTransactionBTN'
					onClick={onCommitTransaction}
				>
				Commit
				</button>
				</div>
			</div>
	);
}

export default InputTransaction;
