import React from 'react';

const Card = ({ id, date, vendor, amount, status, category }) => {

	function getFormattedDate(date) {
	  var year = date.getFullYear();

	  var month = (1 + date.getMonth()).toString();
	  month = month.length > 1 ? month : '0' + month;

	  var day = date.getDate().toString();
	  day = day.length > 1 ? day : '0' + day;

	  return month + '/' + day + '/' + year;
	}

	date = getFormattedDate(date)
	return (
		<div>
			<p className="fl w-20 tc">{date}</p> 
			<p className="fl w-20 tc">{vendor}</p> 
			<p className="fl w-20 tc">{`$ ${amount}`}</p> 
			<p className="fl w-20 tc">{status}</p> 
			<p className="fl w-20 tc">{category}</p>
		</div>
		);

}

export default Card;
