import React, { useState, useEffect } from 'react';
import './AccountBalance.css'

const AccountPending = (props) => {
	const [pendingBalance, setPendingBalance] = useState(0)
	const [pendingColor, setPendingColor] = useState('black')

	useEffect(() => {
		if (props.account_id) {
			try {
				fetch(`${props.host}sumPending`, {
					method: 'post',
					headers: {'Content-Type': 'application/json'},
					body: JSON.stringify({
					  "account_id": props.account_id
					})
				})
					.then(response => response.json())
			      	.then(data => setPendingBalance(data[0].sum));

			} catch (error) {
				console.error(error)
			} 		

			if (pendingBalance < 0) {
				setPendingColor('red') 
			}else {setPendingColor('black')}
		}

	},[props.account_id, pendingBalance, props.host, props.submit])	

	return (
	  	<p className={pendingColor}>{`$ ${((Math.round(pendingBalance) * 100) / 100).toFixed(2)}`} </p>
	);
}

export default AccountPending;
