import React, { useState } from 'react';
import './Card.css';

const Card = (props) => {
	// console.log(props.host)
	const { id, date, vendor, amount, status, category } = props;
	const [s, setS] = useState(status);
	const [edit, setEdit] = useState(false);
	//const [update, forceUpdate] = useState(false);

	function getFormattedDate(date) {
		const d = new Date(date);

	  const year = d.getFullYear();
	  const month = (d.getMonth() + 1)
	  const day = d.getDate();

	  return month + '/' + day + '/' + year;
	}

	const handelStatus = (id, s) => {
		fetch(`${props.host}/updateStatus`, {
			method: 'put',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "id": id
			})
		})
			.then(response => response.json())
			.catch(console.log)
		s === 'Cleared' ? setS('Pending') :	setS('Cleared')
		props.onChange()
	}

	const handelEdit = (id) => {
		setEdit(!setEdit)
	}

	const handelRemove = (id) => {
		fetch(`${props.host}/remove`, {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "id": id
			})
		})
			.then(response => response.json())
			.catch(console.log)
		//props.setCount(!props.count)
		// console.log(`remove ${props.submit}` )
		props.onChange()

	}

	const formatedDate = getFormattedDate(date)
	return (
		<div>
		{ edit === false
		? <div className="flex justify-center">
			<p className="fl w-10 tc">{formatedDate}</p> 
			<p className="fl w-20 tc">{vendor}</p> 
			<p className="fl w-10 tc">{amount}</p> 
			<button 
				className="fl w-10 tc bg-white "
				onClick={() => handelStatus(id, s)}
			>{s}
			</button> 
			<p className="fl w-10 tc">{category}</p>
			<button 
				className="fl w-10 tc bg-white" 
				onClick={() => handelEdit(id)}>edit
			</button>
		</div>
		: <div className="flex justify-center">
			<p> Hello World </p>
		</div>
		}
		</div>

		);



}

export default Card;
