import React, { useState } from 'react';
import './Card.css';

const Card = (props) => {
	// console.log(props.host)
	//const { id, date, vendor, amount, status, category } = props;
	const [edit, setEdit] = useState(false);
	const [date, setDate] = useState(props.date)
	const [vendor, setVendor] = useState(props.vendor)
	const [amount, setAmount] =	useState(props.amount)
	// const [status, setStatus] = useState(props.status)
	const [category, setCategory] = useState(props.category)
	const { id } = props;
	const [s, setS] = useState(props.status);
	//const [update, forceUpdate] = useState(false);

	function getFormattedDate(date) {
		const d = new Date(date);

		const year = d.getFullYear();
		const month = (d.getMonth() + 1)
		const day = d.getDate();

	  return month + '-' + day + '-' + year;
	}

	function getStingDate(date) {
		const d = new Date(date);

		const year = d.getFullYear();
		let month = (d.getMonth() + 1)
		let day = d.getDate();

		month < 10 ? month = `0${month}` : month = `${month}`
		day < 10 ? day = `0${day}` : day = `${day}`;

	  return `${year}-${month}-${day}`;
	}

	const handelStatus = (id, s) => {
		fetch(`${props.host}/updateStatus`, {
			method: 'put',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "id": id
			})
		})
			.then(response => response.json())
			.catch(console.log)
		s === 'Cleared' ? setS('Pending') :	setS('Cleared')
		props.onChange()
	}

	const handelEdit = (id) => {
		//console.log(edit)
		setEdit(!edit)
	}

	const handelRemove = (id) => {
		fetch(`${props.host}/remove`, {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "id": id
			})
		})
			.then(response => response.json())
			.catch(console.log)
			//console.log(id)
		props.onChange()

	}

	const onDateChange = (event) => {
		setDate(event.target.value)
	}

	const onVendorChange = (event) => {
		setVendor(event.target.value)
	}

	const onAmountChange = (event) => {
		setAmount(event.target.value)
	}

	const onCategoryChange = (event) => {
		setCategory(event.target.value)
	}

	// const onCommitIncome = () => {
	// 	onCommitTransaction(amount)
	// }

	// const onCommitExpense = () => {
	// 	onCommitTransaction(-amount)
	// }

	const onUpdateTransaction = () => {
		fetch(props.host+'/update', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "date": date,
			  "vendor": vendor,
			  "category": category,
			  "status": props.status,
			  "amount": amount,
			  "id": id
			})
		})
			.then(response => response.json())
			.catch(console.log)
		setVendor(vendor)
		setDate(stringDate)
		setAmount(amount)
		setCategory(category)
		handelEdit(id)
		// console.log(`insert ${props.submit}`)
		props.onChange()

	}

	const formatedDate = getFormattedDate(date)
	const stringDate = getStingDate(date)
	const removeTransactions = handelRemove(id)
	return (
		<div>
		{ edit === false
		? <div className="flex justify-center">
			<p className="fl w-10 tc">{formatedDate}</p> 
			<p className="fl w-20 tc">{vendor}</p> 
			<p className="fl w-10 tc">{amount}</p> 
			<button 
				className="fl w-10 tc bg-white "
				onClick={() => handelStatus(id, s)}
			>{s}
			</button> 
			<p className="fl w-10 tc">{category}</p>
			<button 
				className="fl w-10 tc bg-white" 
				onClick={() => handelEdit(id)}>edit
			</button>
		</div>
		: <div className="flex justify-center">
			<p className="fl w-10 tc">{id}</p> 
			<input 
				className= 'f4 pa2 w-60 center' 
				type='date' 
				id='date'
				name='date'
				value={stringDate}
				onChange={onDateChange}
			/>
			<input 
				className= 'f4 pa2 w-70 center' 
				type= 'text' 
				placeholder="Vendor"
				id='Vendor'
				name='Vendor'
				value={vendor}
				onChange={onVendorChange}
			/>
			<input 
				className= 'f4 pa2 w-70 center' 
				type= 'double' 
				placeholder="Amount"
				id='Amount'
				name='Amount'
				value={amount}
				onChange={onAmountChange}
			/>
			<input 
				className= 'f4 pa2 w-70 center' 
				type= 'tex' 
				placeholder="Category"
				id='Category'
				name='Category'
				value={category}
				onChange={onCategoryChange}
			/>
			<button onClick={handelEdit}>Cancel </button>
			<button onClick={onUpdateTransaction}>Update </button>
			<button onClick={removeTransactions}>Del </button>
		</div>
		}
		</div>

		);



}

export default Card;
