			.then(response => response.json())
	      	.then(data => {setSum(data)});
	    	//.then(data => res.json(data))
	    // count++
	    // console.log(count)

	},[])

	return (
		<div>
		  <div className='f3'>
		    {`${props.name}, your account ballance is ...`}
		  </div>
		  <div>
		  	{`Pending $1999.44 Actual ${sum}`}
		  </div>
		</div>
	);
}

export default Greeting;
