import React, { useState } from 'react';
import { useForm } from "react-hook-form";

const InputTransaction = (props) => {
	const [date, setDate] = useState('')
	const [vendor, setVendor] = useState('Vendor')
	const [amount, setAmount] =	useState('')
	const [status, setStatus] = useState('Pending')
	const [category, setCategory] = useState('')

	const { register, handleSubmit } = useForm();

	const onStatusChange = (event) => {
		status === 'Pending' ? setStatus('Cleared') : setStatus('Pending')
	}

	const onSubmit = (data, e) => {
		setDate(data.date)
		setVendor(data.vendor)
		setAmount(data.amount)
		setCategory(data.category)

	    console.log(data)
	    e.target.reset();
	    setStatus("Pending")
	  }

	const onError = (errors, e) => console.log(errors, e);

	const onCommitTransaction = () => {
		fetch(props.host+'/insert', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
			  "date": date,
			  "vendor": vendor,
			  "category": category,
			  "status": status,
			  "amount": amount
			})
		})
			.then(response => response.json())
			.catch(console.log)
		props.setCount(!props.count)
		// e.target.reset()
		setVendor('Vendor')
	}

	return (
		<div className='center'>
			<form onSubmit={handleSubmit(onSubmit, onError)} className='form center pa4 br3 shadow-5'>
				<input 
					className= 'f4 pa2 w-70 center' 
					type='date' 
					id='date'
					name='date'
					ref={register}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder={vendor}
					id='Vendor'
					name='Vendor'
					ref={register}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'double' 
					placeholder="Amount"
					id='Amount'
					name='Amount'
					ref={register}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder={status}
					id='status'
					name='status'
					ref={status}
				/>
				<input 
					className= 'f4 pa2 w-70 center' 
					type= 'text' 
					placeholder="Category"
					id='Category'
					name='Category'
					ref={register}
				/>
				<button 
					className= 'w-30 grow f4 link ph3 pv2 dib white bg-light-blue' 
					id='CommitTransactionBTN'
					onClick={onSubmit}
				>
				Commit
				</button>
			</form>

			<button 
          className= 'w-30 link ph3 pv2 dib bg-white' 
          id='CommitTransactionBTN'
          onClick={onStatusChange} 
      		>
        	{status}
		      </button>
		</div>
	);
}

export default InputTransaction;
